mapper.select:
select id, studentName, studentKoreanScore, studentEnglishScore, studentMathScore
from student_table
where id = #{intId}

mapper.update:
update student_table
set studentName = #{studentName},
    studentKoreanScore = #{studentKoreanScore},
    studentEnglishScore = #{studentEnglishScore},
    studentMathScore = #{studentMathScore}
where id = #{id}
